import React, { useState, useEffect } from 'react'; // eslint-disable-line
//import axios from 'axios';
import {
  Card,
  CardHeader,
  CardBody,
  Row,
  Col,
  Table, // eslint-disable-line
  FormGroup,
  Label,
  Input,
  Button,
} from 'reactstrap';


function DoubtsPage() {
  const [studentName, setStudentName] = useState('');
  const [facultyName, setFacultyName] = useState('');
  const [doubtDescription, setDoubtDescription] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    setError('');
    setLoading(true);

    const formData = {
      studentName,
      facultyName,
      doubtDescription,
    };

    try {
      // Simulating form submission by logging the data
      console.log('Form submitted:', formData);

      // Here you can make your API call to submit the form data
      // Example:
      // const response = await axios.post('your-api-endpoint', formData);
      // console.log('Response:', response.data);

      // Reset form fields after submission
      setStudentName('');
      setFacultyName('');
      setDoubtDescription('');
    } catch (error) {
      console.error('Error submitting form:', error);
      setError('Error submitting form. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <h5 className="title">Submit Doubts</h5>
            </CardHeader>
            <CardBody>
              {error && <div className="text-danger">{error}</div>}
              <FormGroup>
                <Label for="studentName">Student Name:</Label>
                <Input
                  type="text"
                  id="studentName"
                  value={studentName}
                  onChange={(e) => setStudentName(e.target.value)}
                />
              </FormGroup>
              <FormGroup>
                <Label for="facultyName">Faculty Name:</Label>
                <Input
                  type="text"
                  id="facultyName"
                  value={facultyName}
                  onChange={(e) => setFacultyName(e.target.value)}
                />
              </FormGroup>
              <FormGroup>
                <Label for="doubtDescription">Doubt Description:</Label>
                <Input
                  type="textarea"
                  id="doubtDescription"
                  value={doubtDescription}
                  onChange={(e) => setDoubtDescription(e.target.value)}
                />
              </FormGroup>
              <FormGroup>
  <Label for="pdfFile">Upload PDF:</Label>
  <Input
    type="file"
    id="pdfFile"
    accept=".pdf"
    onChange={(e) => handleFileChange(e.target.files[0])}
  />
</FormGroup>

              <Button color="primary" onClick={handleSubmit} disabled={loading}>
                Submit
              </Button>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default DoubtsPage;
