import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import Axios for making HTTP requests
import {
  Card,
  CardHeader,
  CardBody,
  Row,
  Col,
  FormGroup,
  Label,
  Input,
  Button,
} from 'reactstrap';

function DoubtsPage() {
 
  const [facultyName, setFacultyName] = useState('');
  const [doubtDescription, setDoubtDescription] = useState('');
  const [pdfFile, setPdfFile] = useState(null); // eslint-disable-line
const [loading, setLoading] = useState(false); // eslint-disable-line
const [error, setError] = useState(''); // eslint-disable-line

  
  const [faculties, setFaculties] = useState([]); // State for faculty names

  useEffect(() => {
    // Fetch student names when component mounts
    
    // Fetch faculty names when component mounts
    fetchFaculties();
  }, []);

 

  const fetchFaculties = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/faculties'); // Change the URL to match your backend endpoint
      setFaculties(response.data);
    } catch (error) {
      console.error('Error fetching faculty names:', error);
    }
  };

  const handleSubmit = async () => {
    // Your form submission logic remains unchanged
    // It will use the values of studentName, facultyName, etc. from state
  };

  const handleFileChange = (file) => {
    setPdfFile(file);
  };

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <h5 className="title">Submit Doubts</h5>
            </CardHeader>
            <CardBody>
              {error && <div className="text-danger">{error}</div>}
             
              <FormGroup>
                <Label for="facultyName">Faculty Name:</Label>
                <Input
                  type="select"
                  id="facultyName"
                  value={facultyName}
                  onChange={(e) => setFacultyName(e.target.value)}
                >
                  <option value="">Select Faculty Name</option>
                  {faculties.map((faculty, index) => (
                    <option key={index} value={faculty.username}>
                      {faculty.username}
                    </option>
                  ))}
                </Input>
              </FormGroup>
              <FormGroup>
                <Label for="doubtDescription">Doubt Description:</Label>
                <Input
                  type="textarea"
                  id="doubtDescription"
                  value={doubtDescription}
                  onChange={(e) => setDoubtDescription(e.target.value)}
                />
              </FormGroup>
              <FormGroup>
                <Label for="pdfFile">Upload PDF:</Label>
                <Input
                  type="file"
                  id="pdfFile"
                  accept=".pdf"
                  onChange={(e) => handleFileChange(e.target.files[0])}
                />
              </FormGroup>
              <Button color="primary" onClick={handleSubmit} disabled={loading}>
                Submit
              </Button>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default DoubtsPage;
