import React from 'react';
import { Card, CardImg, CardImgOverlay, CardBody, CardTitle, Badge, Button } from 'reactstrap';
import './CourseTile.css';

const CourseTile = ({ course, handleSubscribe }) => (
  <Card className="course-tile">
    <CardImg width="100%" src={course.imageUrl} alt={course.name} />
    <CardImgOverlay>
      <CardBody>
        <CardTitle>
          <Badge color="primary">${course.price}</Badge>
          <span className="title">{course.name}</span>
        </CardTitle>
        <CardText>{course.duration}</CardText>
        <Button color="primary" onClick={() => handleSubscribe(course)}>
          Subscribe
        </Button>
      </CardBody>
    </CardImgOverlay>
  </Card>
);

export default CourseTile;
