import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  CardText,
  Button,
  Table,
} from 'reactstrap';
import './CourseTable.css'; // Import CSS file for custom styling

const CourseTile = ({ course, handleSubscribe }) => (
  <Card className="course-tile small">
    <CardHeader className="course-header">
      <h6 className="course-title">{course.name}</h6>
    </CardHeader>
    <CardBody>
      <div className="course-details">
        <p className="course-price">Price: ${course.price}</p>
        <p className="course-duration">Duration: {course.duration}</p>
      </div>
      <Button color="primary" onClick={() => handleSubscribe(course)}>
        Subscribe
      </Button>
    </CardBody>
  </Card>
);

const SubscribedCourseTable = ({ subscriptions }) => (
  <Table className="custom-table">
    <thead>
      <tr>
        <th>sl no</th>
        <th>Course Name</th>
        <th>Price</th>
        <th>Duration</th>
      </tr>
    </thead>
    <tbody>
      {subscriptions.map((subscription, index) => (
        <tr key={subscription._id}>
          <td>{index + 1}</td>
          <td>{subscription.courseId.name}</td>
          <td>${subscription.courseId.price}</td>
          <td>{subscription.courseId.duration}</td>
        </tr>
      ))}
    </tbody>
  </Table>
);

const CourseTable = () => {
  const [courses, setCourses] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);

  useEffect(() => {
    fetchCourses();
    fetchSubscriptions();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/courses');
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
      setCourses([]);
    }
  };

  const fetchSubscriptions = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/subscriptions');
      setSubscriptions(response.data);
    } catch (error) {
      console.error('Error fetching subscriptions:', error);
      setSubscriptions([]);
    }
  };

  const handleSubscribe = async (course) => {
    const confirmSubscription = window.confirm(`Do you want to subscribe to ${course.name} course?`);
    
    if (confirmSubscription) {
      try {
        // Make API call to subscribe to the course
        await axios.post('http://localhost:3001/api/subscribe', { courseId: course._id });
        console.log('Subscribed to course:', course.name);
        alert('Subscription successful!');
        // Refetch subscriptions after successful subscription
        fetchSubscriptions();
      } catch (error) {
        console.error('Error subscribing:', error);
        alert('Failed to subscribe to the course.');
      }
    } else {
      console.log('Subscription cancelled');
    }
  };

  return (
    <div className="content">
      <div className="row">
        <div className="col-md-6">
          <h2>Available Courses</h2>
          <div className="course-list">
  {courses.map((course) => (
    <CourseTile key={course.id} course={course} handleSubscribe={handleSubscribe} />
  ))}
</div>

          
         
        </div>
        <div className="col-md-6">
          <h2>Subscribed Courses</h2>
          <SubscribedCourseTable subscriptions={subscriptions} />
        </div>
      </div>
    </div>
  );
};

export default CourseTable;
