import React, { useState, useEffect } from 'react';
//import axios from 'axios';
import './QandA.css'; // Import your CSS file for styling

function QandA() {
  const [doubts, setDoubts] = useState([]);
  const [newDoubt, setNewDoubt] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [batchId, setBatchId] = useState('');
  const [studentId, setStudentId] = useState('');

  useEffect(() => {
    // Fetch doubts or any initial data if needed
  }, []);

  const handleDoubtChange = (e) => {
    setNewDoubt(e.target.value);
  };

  const handleBatchIdChange = (e) => {
    setBatchId(e.target.value);
  };

  const handleStudentIdChange = (e) => {
    setStudentId(e.target.value);
  };

  const handleSubmitDoubt = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Add doubt submission logic here (e.g., using Axios)
      // Example:
      // const response = await axios.post('http://localhost:3001/api/add-doubt', { doubt: newDoubt });
      // if (response.status === 201) {
      //   setDoubts([...doubts, newDoubt]);
      //   setNewDoubt('');
      // } else {
      //   setError('Failed to submit doubt');
      // }

      // Temporary code to simulate addition of doubt
      setDoubts([...doubts, { batchId, studentId, doubt: newDoubt }]);
      setNewDoubt('');
      setBatchId('');
      setStudentId('');
    } catch (error) {
      console.error('Error submitting doubt:', error);
      setError('Error submitting doubt. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const populateFields = () => {
    // Logic to populate batchId, studentId, and possibly doubts
    // For now, let's just set some sample values
    setBatchId('Batch123');
    setStudentId('Student456');
  };

  return (
    <div className="content">
      <h2 className="cute-heading">Live Class Doubts</h2>
      <div>
        <button className="cute-button" onClick={populateFields}>Populate Fields</button>
      </div>
      <div>
        <form onSubmit={handleSubmitDoubt}>
          <input
            className="cute-input"
            type="text"
            placeholder="Ask your doubt..."
            value={newDoubt}
            onChange={handleDoubtChange}
          />
          <input
            className="cute-input"
            type="text"
            placeholder="Batch ID"
            value={batchId}
            onChange={handleBatchIdChange}
          />
          <input
            className="cute-input"
            type="text"
            placeholder="Student ID"
            value={studentId}
            onChange={handleStudentIdChange}
          />
          <button className="cute-button" type="submit" disabled={loading}>Submit</button>
        </form>
        {error && <div className="text-danger">{error}</div>}
      </div>
      <div>
        <h3 className="cute-heading">Doubts:</h3>
        <ul>
          {doubts.length > 0 ? (
            doubts.map((doubt, index) => (
              <li key={index}>
                Batch: {doubt.batchId}, Student: {doubt.studentId}, Doubt: {doubt.doubt}
              </li>
            ))
          ) : (
            <li>No doubts available</li>
          )}
        </ul>
      </div>
    </div>
  );
}

export default QandA;
