import React, { useState } from 'react';

function DoubtsPage() {
  const [isOpen, setIsOpen] = useState(false);
  const [formData, setFormData] = useState({
    studentName: '',
    facultyName: '',
    doubtDescription: ''
  });

  const openForm = () => {
    setIsOpen(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, you can send formData to your server or do something else
    console.log(formData);
    // Clear form fields
    setFormData({
      studentName: '',
      facultyName: '',
      doubtDescription: ''
    });
    // Close the form
    setIsOpen(false);
  };

  return (
    <div>
      <button onClick={openForm}>Open Form</button>
      {isOpen && (
        <div>
          <h2>Submit Doubts</h2>
          <form onSubmit={handleSubmit}>
            <label htmlFor="studentName">Student Name:</label><br />
            <input type="text" id="studentName" name="studentName" value={formData.studentName} onChange={handleChange} /><br />
            <label htmlFor="facultyName">Faculty Name:</label><br />
            <input type="text" id="facultyName" name="facultyName" value={formData.facultyName} onChange={handleChange} /><br />
            <label htmlFor="doubtDescription">Doubt Description:</label><br />
            <textarea id="doubtDescription" name="doubtDescription" value={formData.doubtDescription} onChange={handleChange} /><br />
            <button type="submit">Submit</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default DoubtsPage;
