import React, { useState } from 'react';

function LiveClass() {
  const [doubts, setDoubts] = useState([]);
  const [newDoubt, setNewDoubt] = useState('');

  const handleDoubtChange = (e) => {
    setNewDoubt(e.target.value);
  };

  const handleSubmitDoubt = (e) => {
    e.preventDefault();
    if (newDoubt.trim() !== '') {
      setDoubts(prevDoubts => [...prevDoubts, newDoubt.trim()]);
      setNewDoubt('');
    }
  };

  return (
    <div>
      <h2>Live Class Doubts</h2>
      <div>
        <form onSubmit={handleSubmitDoubt}>
          <input
            type="text"
            placeholder="Ask your doubt..."
            value={newDoubt}
            onChange={handleDoubtChange}
          />
          <button type="submit">Submit</button>
        </form>
      </div>
      <div>
        <h3>Doubts:</h3>
        <ul>
          {doubts.map((doubt, index) => (
            <li key={index}>{doubt}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default LiveClass;
