import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Card,
  CardHeader,
  CardBody,
  Row,
  Col,
  Table,
  FormGroup,
  Label,
  Input,
  Button,
} from 'reactstrap';

const CourseTable = () => {
  const [courses, setCourses] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchCourses();
    fetchSubscriptions();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await axios.get('/api/courses');
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
      setCourses([]);
    }
  };

  const fetchSubscriptions = async () => {
    try {
      const response = await axios.get('/api/subscriptions');
      setSubscriptions(response.data);
    } catch (error) {
      console.error('Error fetching subscriptions:', error);
      setSubscriptions([]);
    }
  };

  const handleSubscribe = async (courseId) => {
    setLoading(true);
    try {
      await axios.post('/api/subscribe', { courseId });
      await fetchSubscriptions();
    } catch (error) {
      console.error('Error subscribing:', error);
      setError('Failed to subscribe to the course.');
    } finally {
      setLoading(false);
    }
  };

  const handleUnsubscribe = async (subscriptionId) => {
    setLoading(true);
    try {
      await axios.delete(`/api/unsubscribe/${subscriptionId}`);
      await fetchSubscriptions();
    } catch (error) {
      console.error('Error unsubscribing:', error);
      setError('Failed to unsubscribe from the course.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <h5 className="title">Course Table</h5>
            </CardHeader>
            <CardBody>
              {error && <div className="text-danger">{error}</div>}
              <Table responsive>
                <thead className="text-primary">
                  <tr>
                    <th>Course Name</th>
                    <th>Price</th>
                    <th>Duration</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {courses.map((course) => (
                    <tr key={course.id}>
                      <td>{course.name}</td>
                      <td>{course.price}</td>
                      <td>{course.duration}</td>
                      <td>
                        {subscriptions.some((sub) => sub.courseId === course.id) ? (
                          <Button color="danger" onClick={() => handleUnsubscribe(course.id)} disabled={loading}>
                            Unsubscribe
                          </Button>
                        ) : (
                          <Button color="primary" onClick={() => handleSubscribe(course.id)} disabled={loading}>
                            Subscribe
                          </Button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default CourseTable;
