import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CourseTable = () => {
  const [courses, setCourses] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);

  useEffect(() => {
    // Fetch courses and subscriptions data from the server
    axios.get('/api/courses')
      .then(response => {
        setCourses(response.data);
      })
      .catch(error => {
        console.error('Error fetching courses:', error);
      });

    axios.get('/api/subscriptions')
      .then(response => {
        setSubscriptions(response.data);
      })
      .catch(error => {
        console.error('Error fetching subscriptions:', error);
      });
  }, []);

  const handleSubscribe = (courseId) => {
    axios.post('/api/subscribe', { courseId })
      .then(response => {
        // Update subscriptions state
        setSubscriptions([...subscriptions, response.data]);
      })
      .catch(error => {
        console.error('Error subscribing:', error);
      });
  };

  const handleUnsubscribe = (subscriptionId) => {
    axios.delete(`/api/unsubscribe/${subscriptionId}`)
      .then(() => {
        // Update subscriptions state
        setSubscriptions(subscriptions.filter(sub => sub.id !== subscriptionId));
      })
      .catch(error => {
        console.error('Error unsubscribing:', error);
      });
  };

  return (
    <div>
      <h2>Course Table</h2>
      <table>
        <thead>
          <tr>
            <th>Course Name</th>
            <th>Price</th>
            <th>Duration</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {courses.map(course => (
            <tr key={course.id}>
              <td>{course.name}</td>
              <td>{course.price}</td>
              <td>{course.duration}</td>
              <td>
                {subscriptions.some(sub => sub.courseId === course.id) ? (
                  <button onClick={() => handleUnsubscribe(course.id)}>Unsubscribe</button>
                ) : (
                  <button onClick={() => handleSubscribe(course.id)}>Subscribe</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CourseTable;
