import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  CardText,
  Button,
} from 'reactstrap';

const CourseTile = ({ course, subscriptions, handleSubscribe, loading }) => (
  <Card>
    <CardHeader>
      <h5 className="title">{course.name}</h5>
    </CardHeader>
    <CardBody>
      <CardTitle>Price: ${course.price}</CardTitle>
      <CardText>Duration: {course.duration}</CardText>
      {subscriptions.some((sub) => sub.courseId === course.id) ? (
        <Button color="danger" disabled>
          Subscribed
        </Button>
      ) : (
        <Button
          color="primary"
          onClick={() => handleSubscribe(course.id)}
          disabled={loading}
        >
          Subscribe
        </Button>
      )}
    </CardBody>
  </Card>
);

const CourseTable = () => {
  const [courses, setCourses] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchCourses();
    fetchSubscriptions();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/courses');
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
      setCourses([]);
    }
  };

  const fetchSubscriptions = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/subscriptions');
      setSubscriptions(response.data);
    } catch (error) {
      console.error('Error fetching subscriptions:', error);
      setSubscriptions([]);
    }
  };

  const handleSubscribe = async (courseId) => {
    setLoading(true);
    try {
      // Make API call to subscribe to the course
      await axios.post('http://localhost:3001/api/subscribe', { courseId });
      // Refetch subscriptions to update the UI
      await fetchSubscriptions();
    } catch (error) {
      console.error('Error subscribing:', error);
      setError('Failed to subscribe to the course.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="content">
      <div className="row">
        {courses.map((course) => (
          <div className="col-md-4" key={course.id}>
            <CourseTile
              course={course}
              subscriptions={subscriptions}
              handleSubscribe={handleSubscribe}
              loading={loading}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default CourseTable;
